import { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { popularMovies, searchByGenre } from "../features/movies/moviesAction";
import { setAllMovies } from "../features/movies/movieSlice";

function Movielist({ handleCharacterHover }) {

    const { allMoviesData, genres, searchTerm} = useSelector((state) => state.movies);   
    const dispatch = useDispatch();



    useEffect(()=>{
        dispatch(setAllMovies(popularMovies()));

        if(genres){
            dispatch(searchByGenre(genres));
        }
     },[dispatch, genres])

     const data = allMoviesData.filter((item)=> item.results.title.toLowerCase().includes(searchTerm));
     let count =0;
    return (
        <div className="list">
            {data.length > 0 && data.map((item) =>{
            console.log("omo"+allMoviesData);
            return(
                 <div key={count} className="listitem"
                 onMouseEnter={() => handleCharacterHover(item)}
                 onMouseLeave={() => handleCharacterHover(null)}>{item.results[count].title}</div>
                );
            })}

        </div>
    );
};

export default Movielist;
