import { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import popularMovies from "../features/movies/moviesAction";
import { setAllMovies } from "../features/movies/movieSlice";

function Movielist({ handleCharacterHover }) {

    const { allMoviesData, genres, searchTerm} = useSelector((state) => state.movies);   
    const dispatch = useDispatch();

    const [moviesData, setMoviesData]=useState([]);
    useEffect(()=>{
        dispatch(popularMovies());
        setMoviesData(allMoviesData);
     },[dispatch])

    return (
        <div className="list">
            {moviesData && moviesData.map((item, index) =>{
                console.log("item :"+item);
                return (
                        <div key={index} className="listitem"
                            onMouseEnter={() => handleCharacterHover(item)}
                            onMouseLeave={() => handleCharacterHover(null)}
                        >{}</div>
                    );
                 })};
        </div>
    );
};

export default Movielist;
