function Movielist({ handleCharacterHover }) {

    const { allMoviesData, genres, searchTerm} = useSelector((state) => state.movies);   
    const dispatch = useDispatch();

    const [moviesData, setMoviesData]=useState([]);

    useEffect(()=>{
        dispatch(popularMovies());
        setMoviesData(allMoviesData);

     },[dispatch])

     let count =0;
    return (
        <div className="list">
            {moviesData.length > 0 && moviesData.map((item) =>{
            console.log("omo"+moviesData);
            return(
                 <div key={count} className="listitem"
                 onMouseEnter={() => handleCharacterHover(item)}
                 onMouseLeave={() => handleCharacterHover(null)}>{item.results[count].title}</div>
                );
            })}

        </div>
    );
};

export default Movielist;
