    return (
        <section>
            {MovieDetails < 0 ? <h1>Loading...</h1> : MovieDetails.map((item) =>{ 
                return(<div className="profile"><img src={item.banner} alt={item.title} />
                        <h3>Name:  {item.title}</h3>
                        <p>Rating:  {item.rating}</p>
                        <p>year:  {item.year}</p>
                        </div>
                        );
            })};
        </section>
    );
}

export default MovieDetails;
