import { useSelector, useDispatch } from 'react-redux';
import { useEffect, useState } from 'react';
import {
    setAllMovies,
    setFilteredMovies,
    setGenres,
    setSelectedGenre,
} from '../features/movies/movieSlice';
import popularMovies  from '../features/movies/moviesAction';

function Search() {
    const [query, setQuery] = useState("");
    const dispatch = useDispatch();
    const { allMovies, genres, selectedGenre } = useSelector(
        (state) => state.movies); 


    const filteredMovies = function (allMovies) {
        return allMovies.filter((item) =>
        item.results.title.toLocaleLowerCase().includes(query.toLocaleLowerCase()))
     };
     useEffect(()=>{
        dispatch(setAllMovies(popularMovies()));
     },[dispatch])

    useEffect(()=>{

        dispatch(setFilteredMovies(filteredMovies(allMovies)));
    },[dispatch]);

    return (
        <div className="search_container">
            <input type="text" placeholder="search..." className="search" onChange={(e) => setQuery(e.target.value)} />
        </div>
    );
};

export default Search;
