import { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import popularMovies from "../features/movies/moviesAction";

function Movielist({ handleCharacterHover }) {

    const { allMovies, filteredMovies, genres, selectedGenre } = useSelector(
        (state) => state.movies);   
    const dispatch = useDispatch();

    // useEffect(()=>{
    //     dispatch(setAllMovies(popularMovies()));
    //     console.log("want"+allMovies);
    //  },[])

    return (
        <div className="list">
            {filteredMovies && filteredMovies.map((item, index) =>{
                return (
                        <div key={index} className="listitem"
                            onMouseEnter={() => handleCharacterHover(item)}
                            onMouseLeave={() => handleCharacterHover(null)}
                        >{item.results.title}</div>
                    );
                 })};
        </div>
    );
};

export default Movielist;
