import { useDispatch, useSelector } from "react-redux";
import { useState, useEffect } from "react";

import { setMovieId } from "../features/movies/movieSlice";
import { searchMovieDetail } from "../features/movies/moviesAction";

function MovieDetails() {
    const { movieData, movieId } = useSelector((state) => state.movies);  
    const dispatch = useDispatch();
    // console.log(data);

    useEffect(()=>{
        if(data !=''){
            dispatch(setMovieId(data));
        }
        if(movieId){
            dispatch(searchMovieDetail(movieId));
        }

     },[dispatch, movieId]);

    return (
            <div className="profile"><img src={movieData.banner} alt={movieData.title} />
                        <h3>Name:  {movieData.title}</h3>
                        <p>Rating:  {movieData.rating}</p>
                        <p>year:  {movieData.year}</p>
                        </div>

    );
}

export default MovieDetails;
