import { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { popularMovies, searchByGenre } from "../features/movies/moviesAction";

function Movielist({ handleCharacterHover }) {

    const { allMoviesData, genres, searchTerm} = useSelector((state) => state.movies);   
    const dispatch = useDispatch();


    useEffect(()=>{
        dispatch(popularMovies());

        if(genres){
            dispatch(searchByGenre(genres));
        }
     },[dispatch, genres]);

    //  const data = allMoviesData.filter((item)=> item.results.title.toLowerCase().includes(searchTerm));
     console.log(allMoviesData);
    return (
        <div className="list">

            {allMoviesData.length < 0 ? <h1>Loding...</h1>:allMoviesData.map((item) =>{
            console.log(item);
            return(
                 <div key={item.id} className="listitem"
                 onMouseEnter={() => handleCharacterHover(item)}
                 onMouseLeave={() => handleCharacterHover(null)}>{item.title}</div>
                );
            })}

        </div>
    );
};

export default Movielist;
